class Solution {
public:
    long long f(vector<int>&a, vector<int>&b, int n)
{
    long long operations = 0;
    long long i = 0, prev = 0;

    while (i < n) {
        if (a[i] - b[i] == 0) {
            i++;
            prev = 0;
            continue;
        }


        int diff = a[i] - b[i];
        i++;

        while (i < n &&
           a[i] - b[i] == diff) {
            i++;
        }
        long long p1 = diff;
     //   cout<< diff << endl;
         if( (prev < 0 && diff < 0)  ||  (prev >= 0 && diff >=0) ) {
              long long k1 = abs(diff), k2 = abs(prev);
              diff = max(0ll, k1 - k2);
         }
        prev = p1;
       // cout< 
        operations += abs(diff);
    }
        return operations;

    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
         return f(nums, target, nums.size());
    }
};
