class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long operations = 0;
        int n = nums.length;
        long[] diff = new long[n];
        for(int i=0; i<n; i++)diff[i] = (long)target[i]-nums[i];
        for(int i=0; i<n; i++) 
        {
            if(diff[i]!=0) 
            {
                if(i>0 && ((diff[i]>0 && diff[i-1]>0) || (diff[i]<0 && diff[i-1]<0)))
                {
                    if(Math.abs(diff[i])>Math.abs(diff[i-1]))operations+=Math.abs(diff[i])-Math.abs(diff[i-1]);
                }
                else operations+=Math.abs(diff[i]);
            }
        }
        return operations;
    }
}


