#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define mod 1000000007
#define ff first
#define ss second
#define mk make_pair
#define pb push_back
#define rep(i, start, end) for (int i = start; i < end; i++)
#define ld long double
using ii = pair<int, int>;
const static int mx = 2e5 + 100;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>arr;
        int n=(int)nums.size();
        for(int i=0;i<n;i++)
        {
            arr.push_back(target[i]-nums[i]);
        }
        int last=0;
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            if((last*1LL*arr[i])<=0)
            {
                ans+=abs(arr[i]);
                last=arr[i];
            }
            else 
            {
                if(arr[i]>=0)
                {
                    ans+=max(0,arr[i]-last);
                    last=arr[i];
                }
                else
                {
                    ans+=max(0,last-arr[i]);
                    last=arr[i];
                }
            }
        }
        return ans;
    }
};
