class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff(target.size());
    for (int i=0;i<nums.size();i++) {
        diff[i]=target[i]-nums[i];
    }

    long long to = 0;
    int cur = 0;

    for (int i = 0;i<diff.size();i++) {
        if (i==0 or (diff[i-1]<0 &&diff[i]<0)) {
            int op=abs(diff[i])-abs(cur);
            if (op>0) {
                to+=op;
            }
            cur = diff[i];
        }
        else if((diff[i-1]>0 && diff[i]>0)){
            int op=abs(diff[i])-abs(cur);
            if (op>0) {
                to+=op;
            }
            cur = diff[i];
        }
        else {
            to += abs(diff[i]);
            cur = diff[i];
        }
    }

    return to;
    }
};
