class Solution {
public:
    long long help(vector<int> &temp, int a, int b){
        long long n = temp.size(), ans = 0;
        cout << a << " " << b << endl;

        for(int i=a;i<=b;i++){
            int m = INT_MAX, x = 0, y = i;
            if(temp[i] == 0)    continue;
            for(int j=i;j<=b;j++){
                if(temp[j] == 0)    break;
                x = j;
                m = min(m, abs(temp[j]));
            }

            for(int j=x;j>=y;j--){
                if(temp[j] < 0) temp[j] += m;
                else if(temp[j] > 0)    temp[j] -= m;
            }   
            if(temp[i]) i--;
            ans += m;
        }

        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long  n = nums.size(), ans = 0, l;
        vector<int> temp;

        for(int i=0;i<n;i++){
            l = target[i] - nums[i];
            temp.push_back(l);
        }

        for(auto a:temp)    cout << a << " ";
        cout << endl;

        long long m = 0, j = 0;
        bool f = (nums[0] >= 0);
        for(int i=0;i<n;i++){
            if(f){
                if(temp[i] < 0){
                    ans += help(temp, j, i-1);
                    j = i;
                    f = 0;
                }
                if(i == n-1){
                    ans += help(temp, j, i);
                }
            }
            else{
                if(temp[i] >= 0){
                    ans += help(temp, j, i-1);
                    j = i;
                    f = 1;
                }
                if(i == n-1){
                    ans += help(temp, j, i);
                }
            }
        }

        return ans;
    }
};
