class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        int len=nums.size();
        vector<int> q1(len+1);
        vector<int> q2(len+1);
        q1[0]=q2[0]=0;
        for(int i=0;i<len;i+=1){
            q1[i+1]=q2[i+1]=target[i]-nums[i];
            if(q1[i]<0)
                q1[i]=0;
            if(q2[i]>0)
                q2[i]=0;
        }

        for(int i=1;i<=len;i+=1){
            if(q1[i-1]<q1[i]){
                res+=q1[i]-q1[i-1];
            }
        }
        for(int i=1;i<=len;i+=1){
            if(q2[i-1]>q2[i]){
                res+=q2[i-1]-q2[i];
            }
        }
        return res;
    }
};
