class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=target.size();
        vector<ll> v(n);
        ll ans=0;
        for(int i=0;i<n;i++){
            v[i]=target[i]-nums[i];
        }

        int i=0;

        while(i<n){
            while(i<n && v[i]==0) i++;
            if(i<n && v[i]>0){
                while(i<n && v[i]>0){
                   if(i+1>=n || v[i+1]<=0 ){
                    ans+=v[i];
                   }
                   else if(i+1<n && v[i+1]<=v[i]){
                    ans+=v[i]-v[i+1];
                   }
                   i++;
                }
            }

            if(i<n &&  v[i]<0){
                while(i<n && v[i]<0){
                   if(i+1>=n || v[i+1]>=0 ){
                    ans+=abs(v[i]);
                   }
                   else if(i+1<n && abs(v[i+1])<=abs(v[i])){
                    ans+=abs(v[i])-abs(v[i+1]);
                   }
                   i++;
                }
            }

        }
        return ans;
    }
};
