class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long res0 = 0;
        long res1 = 0;
        int n = nums.length;
        int[] ansNums = new int[n + 1];
        int[] ansTarget = new int[n + 1];
        for(int i = 0; i < n; i++){
            ansNums[i] += nums[i];
            ansNums[i + 1] -= nums[i];
            ansTarget[i] += target[i];
            ansTarget[i + 1] -= target[i];
            if(ansNums[i] - ansTarget[i] < 0){
                res0 += ansNums[i] - ansTarget[i];
            }else{
                res1 += ansNums[i] - ansTarget[i];
            }
        }
        return Math.max(-res0,res1);
    }
}
