class Solution:
    def minimumOperations(self, nums, target):
        old=0
        diff=0
        c=0
        res=0
        n = len(nums)
        for i in range(n):
            if old== 0:
                if nums[i] < target[i]:
                    old= 1
                    diff = target[i] - nums[i]
                    c += 1
                elif nums[i] > target[i]:
                    old= -1
                    diff = nums[i] - target[i]
                    c += 1
                else:
                    continue
            else:
                if (nums[i] < target[i] and old== 1 and diff == target[i] - nums[i]) or \
                   (nums[i] > target[i] and old== -1 and diff == nums[i] - target[i]):
                    c += 1
                    continue
                elif nums[i] < target[i] and old== 1:
                    if target[i] - nums[i] > diff:
                        c = 1
                        diff = target[i] - nums[i]
                    else:
                        res+= diff - target[i] + nums[i]
                        diff = target[i] - nums[i]
                    continue
                elif nums[i] > target[i] and old== -1:
                    if nums[i] - target[i] > diff:
                        c = 1
                        diff = nums[i] - target[i]
                    else:
                        res+= diff + target[i] - nums[i]
                        diff = nums[i] - target[i]
                    continue
                elif nums[i] == target[i]:
                    res+= diff
                    old= 0
                    c = 0
                    diff = 0
                else:
                    res+= diff
                    if nums[i] < target[i]:
                        old= 1
                        diff = target[i] - nums[i]
                        c += 1
                    else:
                        old= -1
                        diff = nums[i] - target[i]
                        c += 1

        return res+ diff
