class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<vector<long long>>temp;
        int i=0;
        while(i<nums.size())
        {
            int s;
            if(nums[i]>target[i])
            {
                vector<long long>t;
                while(i<nums.size() && nums[i]>target[i])
                {
                    t.push_back(nums[i]-target[i]);
                    i++;
                }
                t.push_back(1);
                temp.push_back(t);
            }
            else if(i<nums.size() && nums[i]<target[i])
            {
                vector<long long>t;
                while(i<nums.size() && nums[i]<target[i])
                {
                    t.push_back(nums[i]-target[i]);
                    i++;
                }
                t.push_back(-1);
                temp.push_back(t);
            }
            else i++;
        }
        long long ans=0;


        // for(auto it:temp)
        // {
        //         for(int i=0;i<it.size();i++)cout<<it[i]<<" ";
        //     cout<<endl;
        // }

        for(auto it:temp)
        {
            if(it[0]>0)
            {
                for(int i=0;i<it.size()-1;i++)
             {
                if(it[i]!=1)
                {
                   ans+=max((it[i]-it[i+1]),0LL);
                }

             }
                ans++;
            }
            else
            {
                 for(int i=0;i<it.size()-1;i++)
             {
                if(it[i]!=-1)
                {
                   if(it[i]<it[i+1])
                   {
                       ans+=abs(it[i]-it[i+1]);
                   }
                }

             }
                ans++;
                // cout<<ans<<endl;
            }
        }
        return ans;
    }
};
