class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        vector<long long> temp(n,0);

        for(int i=0;i<n;i++){
            temp[i] = target[i]-nums[i];
        }
        long long prev = 1e9;
        for(int i=0;i<n;i++){
            if(i==0){
                prev = temp[i];
                if(temp[i]<0) temp[i] *= (-1);
                ans += temp[i];
            }
            else if(temp[i]==0){
                prev = 0;
            }
            else if(temp[i]>0){
                if(prev<=0){
                    prev = temp[i];
                    ans += temp[i];
                }
                else{
                    if(prev<temp[i]){
                        ans += temp[i]-prev;
                        prev = temp[i];
                    }
                    prev = temp[i];
                }
                //cout<<temp[i]<<" "<<ans<<"q ";
            }
            else{
                if(prev<=0){
                    if(prev>temp[i]){
                        ans += (prev-temp[i]);
                        prev = temp[i];
                    }
                    prev = temp[i];
                }
                else{
                    prev = temp[i];
                    ans += (-1)*temp[i];
                }
                //cout<<temp[i]<<" "<<ans<<"q ";
            }
        }

        return ans;
    }
};
