class Solution {
public:

    long long fun(int start,int end,vector<int>&diff){
        // cout<<start<<" "<<end<<endl;
        if(start==end){
            return abs(diff[start]);
        }
        long long ans=0;
        for(int i=start;i<=end;i++)diff[i]=abs(diff[i]);

        ans=diff[start];
        for(int i=start+1;i<=end;i++){
            if(diff[i]>diff[i-1]){
                ans+=(diff[i]-diff[i-1]);
            }
        }
        return ans;

    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int> diff(nums.size());
        for(int i=0;i<nums.size();i++){
            diff[i]=target[i]-nums[i];
        }
        int start=0,end=0;
        while(start<diff.size()){
            if(end==diff.size()){
                ans+=fun(start,end-1,diff);
                break;
            }
            if(diff[start]>=0 && diff[end]>=0){
                end++;
            }
            else if(diff[start]<0 && diff[end]<0){
                end++;
            }else{
                ans+=fun(start,end-1,diff);
                start=end;
                // end=end+1;
            }

        }
        return ans;
    }
};
