class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       long long n = nums.size();
        vector<long long> v(n);
        for (int i = 0; i < n; ++i) {
            v[i] = target[i] - nums[i];
        }
        long long tp =0, cd = 0;
        for (int it = 0; it < n; it++) {
            if (it == 0 || (v[it - 1] > 0 && v[it] > 0) || (v[it - 1] < 0 && v[it] < 0)) {
                int ad = abs(v[it]) - abs(cd);
                if (ad > 0) {
                    tp += ad;
                }
                cd = v[it];
            } else {
                tp += abs(v[it]);
                cd = v[it];
            }
        }
        return tp;
    }
};
