class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>a(n+1);
        for(int i = 1; i<=n; i++){
            a[i] = nums[i-1]-target[i-1];
        }
        vector<int>d(n+1);
        for(int i = 1; i<=n; i++){
            d[i] = a[i]-a[i-1];
        }
        long long x = 0;
        long long y = 0;
        for(int i = 1; i<=n; i++){
            if(d[i] > 0){
                x+=d[i];
            }
            else{
                y+=abs(d[i]);
            }
        }
        return max(x,y);
    }
};
