class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def help(arr):
            return [y - x for x, y in pairwise([0] + arr)]
        a = help(nums)
        b = help(target)
        v1 = v2 = 0
        for x, y in zip(a, b):
            if x >= y:
                v1 += x - y
            else:
                v2 += y - x
        return max(v1, v2)
