class Solution {
public:
    int minNumberOperations(vector<long long>& target) {
        long long ans=target[0]; int n=target.size();
        for(int i=1;i<n;i++){
            if(target[i]>target[i-1]) ans+=target[i]-target[i-1];
        }

        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        long long ans=0,n=nums.size();
        vector<long long> target(n);
        for(int i=0;i<n;i++) target[i]=(t[i]-nums[i])*1ll;
        int i=0;
        while(i<n){
            if(target[i]==0){
                i++; continue;
            }
            int j=i;
            while(j<n && (target[j]*target[i]>0)) j++;
            vector<long long> v;
            for(int k=i;k<j;k++) v.push_back(abs(target[k]));
            ans=ans+minNumberOperations(v);
            i=j;
        }
        return ans;
    }
};
