class Solution {
public:
    int solve(int i, int j, const std::vector<int>& diff) {
        int x = diff[i];

        for (int itr = i+1; itr <= j && itr < diff.size(); itr++) {
            x += max(0, diff[itr]-diff[itr-1]);
        }
        return x;
    }

    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        int n = nums.size();
        long long ans = 0; 
        vector<int> diff(n);


        for (int i = 0; i < n; i++) {
            diff[i] = abs(target[i] - nums[i]);
        }

        for (int i = 0; i < n; ) {

            int temp = target[i] - nums[i];
            bool pos = (temp > 0);
            int j = i;
            for (j = i + 1; j < n; j++) {
                if ((pos && target[j] - nums[j] < 0) || (!pos && target[j] - nums[j] > 0)) {
                    j--;
                    break;
                }
            }
            cout<<i<<" "<<j<<endl;
            ans += solve(i, j, diff); 
            i = j+1 ; 
        }
        return ans;
    }
};
