#include <bits/stdc++.h>
using namespace std;

#define ll long long int
#define pb push_back
#define all(v) v.begin(), v.end()
#define sortv(v) sort(all(v))
#define sorta(a,n) sort(a,a+n)
#define rsortv(v) sort(all(v), greater<int>())
#define rsorta(a,n) sort(a, a + n, greater<int>())
#define revv(v) reverse(all(v))
#define reva(a,n) reverse(a, a+n)

#define ct(x,v) count(all(v),x)
#define cto(v) count_if(all(v),[] (int a) {return a%2 == 1;})
#define cte(v) count_if(all(v),[] (int a) {return a%2 == 0;})

#define suma(a,n) accumulate(a,a+n,0)
#define sumv(v) accumulate(all(v),0)

//Containers
typedef pair<int, int>  pii;
typedef pair<ll, ll>    pll;
typedef vector<int>			vi;
typedef vector<ll>			vll;
typedef vector<pii>			vpii;
typedef vector<pll>			vpll;
typedef vector<vi>			vvi;
typedef vector<vll>			vvl;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int p = 0, diff = 0, steps = 0;
        long long result = 0;
        int len = a.size();

        for (int i = 0; i < len; ++i) {
            if (p == 0) {
                if (a[i] < b[i]) {
                    p = 1;
                    diff = b[i] - a[i];
                    steps += 1;
                } 
                else if (a[i] > b[i]) {
                    p = -1;
                    diff = a[i] - b[i];
                    steps += 1;
                } 
                else {
                    continue;
                }
            } 
            else {
                if ((a[i] < b[i] && p == 1 && diff == b[i] - a[i]) || (a[i] > b[i] && p == -1 && diff == a[i] - b[i])) {
                    steps += 1;
                    continue;
                } 
                else if (a[i] < b[i] && p == 1) {
                    if (b[i] - a[i] > diff) {
                        steps = 1;
                        diff = b[i] - a[i];
                    } 
                    else {
                        result += (diff - b[i] + a[i]);
                        diff = b[i] - a[i];
                    }
                    continue;
                } 
                else if (a[i] > b[i] && p == -1) {
                    if (a[i] - b[i] > diff) {
                        steps = 1;
                        diff = a[i] - b[i];
                    } 
                    else {
                        result += (diff + b[i] - a[i]);
                        diff = a[i] - b[i];
                    }
                    continue;
                } 
                else if (a[i] == b[i]) {
                    result += diff;
                    p = 0;
                    steps = 0;
                    diff = 0;
                } 
                else {
                    result += diff;
                    if (a[i] < b[i]) {
                        p = 1;
                        diff = b[i] - a[i];
                        steps += 1;
                    } 
                    else {
                        p = -1;
                        diff = a[i] - b[i];
                        steps += 1;
                    }
                }
            }
        }

        return (result + diff);
    }
};
