class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long p = 0, d = 0, s = 0, ans = 0;
        for (int i = 0; i < n; i++) {
            if (p == 0) {
                if (nums[i] < target[i]) {
                    p = 1;
                    d = target[i] - nums[i];
                    s += 1;
                } else if (nums[i] > target[i]) {
                    p = -1;
                    d = nums[i] - target[i];
                    s += 1;
                } else {
                    continue;
                }
            } else {
                if ((nums[i] < target[i] && p == 1 && d == target[i] - nums[i]) ||
                    (nums[i] > target[i] && p == -1 && d == nums[i] - target[i])) {
                    s += 1;
                    continue;
                } else if (nums[i] < target[i] && p == 1) {
                    if (target[i] - nums[i] > d) {
                        s = 1;
                        d = target[i] - nums[i];
                    } else {
                        ans += static_cast<long long>(d - target[i] + nums[i]);
                        d = target[i] - nums[i];
                    }
                    continue;
                } else if (nums[i] > target[i] && p == -1) {
                    if (nums[i] - target[i] > d) {
                        s = 1;
                        d = nums[i] - target[i];
                    } else {
                        ans += static_cast<long long>(d + target[i] - nums[i]);
                        d = nums[i] - target[i];
                    }
                    continue;
                } else if (nums[i] == target[i]) {
                    ans += static_cast<long long>(d);
                    p = 0; s = 0; d = 0;
                } else {
                    ans += static_cast<long long>(d);
                    if (nums[i] < target[i]) {
                        p = 1;
                        d = target[i] - nums[i];
                        s += 1;
                    } else {
                        p = -1;
                        d = nums[i] - target[i];
                        s += 1;
                    }
                }
            }
        }
        return ans + static_cast<long long>(d);
    }
};
