#include <vector>
#include <cstdlib>  // For std::abs

class Solution {
public:
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        int n = nums.size();
        vector<int> d(n);
        long long ops = 0;
        int cur_seg_diff = 0;

        for (int i=0;i<n;i++) 
        {
            d[i]=target[i]-nums[i];
        }


        for (int i=0;i<n;i++) 
        {
            if (i==0|| (d[i-1]>0 && d[i]>0)||(d[i-1]<0 && d[i]<0)) {
                int add_ops=abs(d[i])-abs(cur_seg_diff);
                if (add_ops>0)
                {
                    ops+=add_ops;
                }
                cur_seg_diff=d[i];
            } else {
                ops+=abs(d[i]);
                cur_seg_diff=d[i];
            }
        }
        return ops;
    }
};
