class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long>a(n+1);
        for(int i=0;i<n;i++)a[i+1]=target[i]-nums[i];

        for(int i=n;i>=1;i--)a[i]=a[i]-a[i-1];

        long long zs=0,fs=0;
        for(int i=1;i<=n;i++)
            if(a[i]>0)zs+=a[i];
            else if(a[i]<0)fs-=a[i];
        return max(zs,fs);
    }
};
