class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long prev = 0, ans = 0, n = nums.size();
        vector<long long> v(n);
        for (int i = 0; i < n; i++)
            v[i] = nums[i] - target[i];
        for (long long curr : v) {
            if (prev >= 0 && curr >= 0 && prev <= curr)
                ans += curr - prev;
            else if (prev <= 0 && curr <= 0 && curr <= prev)
                ans += prev - curr;
            else if ((prev >= 0 && curr <= 0) || (curr >= 0 && prev <= 0))
                ans += abs(curr);
            prev = curr;
        }
        return ans;
    }
};
