class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        # 作差
        n = len(nums)
        diff = [0] * n
        for i in range(n):
            diff[i] = nums[i] - target[i]

        # 
        # 1,2,3,4,5
        # 1,-1,2,-1,2

        # 思维题
        res = 0
        pre = 0
        for v in diff:
            if v == 0:
                pre = 0
            elif v > 0:
                if v > pre:
                    res += min(v - pre, v)
                    pre = v
                elif v < pre:
                    pre = v
            else:
                if v < pre:
                    res += min(pre - v, -v)
                    pre = v
                elif v > pre:
                    pre = v
        return res
