typedef long long ll;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>a(n+2),d(n+2);
        ll ans=0,p=0,q=0;
        for(int i=2;i<=n+1;i++) a[i]=nums[i-2]-target[i-2];
        for(int i=1;i<=n+1;i++) d[i]=a[i]-a[i-1];
        for(int i=1;i<=n+1;i++)
        {
            if(d[i]<0) p-=d[i];
            else q+=d[i];
        } 
        ans=max(p,q);
        return ans;
    }
};
