class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long > d;
        for(int i=0;i<nums.size();i++){
            d.push_back(nums[i]-target[i]);
        }
        long long ans =0;
        long long s = 0;
        long long req = 0;
        for(int i=0;i<d.size();i++){
            long long cur = abs(d[i]);
            if(d[i]==0){
                   // cout<<ans<<"\n";
                    req = 0;
                  s=0;continue;
            }
            if(d[i]<0){
                if(s==0||s==1) {s=-1;req=0;}
                if(req==0) {req = cur;
                ans = ans + cur;}
                else{
                   req = min(req,cur);
                   ans = ans + (cur-req);
                   req = max(req,cur);
                }

            }
            else{
                  if(s==0||s==-1) {s=1;req=0;}
                  if(req==0) {req = cur;
                ans = ans + cur;}
                else{
                   req = min(req,cur);
                   ans = ans + (cur-req);
                        req = max(req,cur);
                }
            }
          //  cout<<ans<<"\n";

        }
        return ans;

    }
};
