class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int answer=0;


        int diff=0;
        long long int flag=0;

        int inc=0;// -1 0 1
        int start=0;
        // int test=0;
        while(flag!=nums.size()){
            // test++;
            inc=0;
            start=0;
            flag=0;
            for(int i=0;i<nums.size();i++){
                if(nums[i]==target[i]){
                    if(inc!=0){
                        if(inc==-1){
                            for(int ij=start;ij<=i-1;ij++){
                                nums[ij]-=diff;
                            }
                            answer+=diff;
                        }
                        else if(inc==1){
                            for(int ij=start;ij<=i-1;ij++){
                                nums[ij]+=diff;
                            }
                            answer+=diff;
                        }
                    }
                    diff=0;
                    flag++;
                    start=i;
                }
                else if(nums[i]<target[i]){//1
                    if(inc!=1){
                        if(inc==-1){
                            for(int ij=start;ij<=i-1;ij++){
                                nums[ij]-=diff;
                            }

                            answer+=diff;
                        }
                        diff=INT_MAX;
                        start=i;
                        inc=1;
                    }
                    // cout<<i<<" "<<target[i]<<" "<<nums[i]<<"&"<<diff<<endl;
                    diff=min(diff,target[i]-nums[i]);
                    // cout<<diff<<endl;
                }
                else{//-1
                    if(inc!=-1){
                        if(inc==1){
                            for(int ij=start;ij<=i-1;ij++){
                                nums[ij]+=diff;
                            }
                            answer+=diff;
                        }
                        diff=INT_MAX;
                        start=i;
                        inc=-1;
                    }
                    diff=min(diff,nums[i]-target[i]);
                }
            }
            if(inc==-1){
                for(int ij=start;ij<nums.size();ij++){
                    nums[ij]-=diff;
                }
                answer+=diff;
            }
            else if(inc==1){
                for(int ij=start;ij<nums.size();ij++){
                    nums[ij]+=diff;
                }
                answer+=diff;
            }
            // cout<<endl;
            // for(auto i: nums){
            //     cout<<i<<" ";
            // }
            // cout<<endl;
            // if(test==21)
            //     break;
        }


        return answer;
    }
};
