class Solution {



    public long minimumOperations(int[] nums, int[] target) {

        long ans=0;

        int diff[]=new int[nums.length];
        for(int i=0;i<nums.length;i++)
        {
            if(target[i]-nums[i]>=0)
            {
                diff[i]=target[i]-nums[i];
            }
            else
            {
                diff[i]=target[i]-nums[i];
            }
           // System.out.println(diff[i]);
        }
        ans=Math.abs(diff[0]);


       for(int i=1;i<nums.length;)
       {
        if(diff[i]>0)
        {
             long c=diff[i];
            if(diff[i-1]<=0)
            {
                ans+=c;

                i++;
                continue;

            }
            else
            {

                if(diff[i]>diff[i-1])
                {
                    ans+=diff[i]-diff[i-1];
                }
                c=0;
                i++;
            }


            while(i<nums.length&&diff[i]>0)
            {
                if(diff[i]>diff[i-1])
                {
                    c+=diff[i]-diff[i-1];

                }
                i++;


            }
           // System.out.println(c);
            ans+=c;

        }
        else
        {

             long c=Math.abs(diff[i]);
            if(diff[i-1]>0)
            {
                ans+=c;
                i++;
                continue;

            }
            else
            {
               if(diff[i]<diff[i-1])
               {
                ans+=Math.abs(diff[i])-Math.abs(diff[i-1]);
               }
                c=0;
                i++;
            }

            while(i<nums.length&&diff[i]<0)
            {
                if(diff[i]<diff[i-1])
                {
                    c+=Math.abs(diff[i])-Math.abs(diff[i-1]);

                }
                i++;


            }
            ans+=c;

        }



       }

       return ans;


    }
}
