class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       int n = nums.size();
        long long prev=0;
        long long ans =0;
        for(int i=0;i<n;i++)
        {
            long long curr = target[i]-nums[i];
            if(i==0 or (curr>0 && prev>0) or (curr<0 && prev<0)){
                int temp = (abs(curr)-abs(prev));
                if(temp>0)
                    ans+=temp;
            }
            else
                ans+=abs(curr);
            prev=curr;
        }
        return ans;
    }
};
