class Solution {
public:
     long long minimumOperations(vector<int>& sankhya, vector<int>& lakshya) {
        vector<int> antar = banaoAntar(sankhya, lakshya);
        return gananaKaryavahi(antar);
    }

private:
    vector<int> banaoAntar(vector<int>& sankhya, vector<int>& lakshya) {
        int lambaai = sankhya.size();
        vector<int> antar(lambaai);
        for (int i = 0; i < lambaai; i++) {
            antar[i] = lakshya[i] - sankhya[i];
        }
        return antar;
    }

    long long gananaKaryavahi(vector<int>& antar) {
        long long kulKaryavahi = 0;
        int vartamanAntar = 0;

        for (int i = 0; i < antar.size(); i++) {
            if (pehlaYaSamaanAntar(i, antar)) {
                long long sahayakKaryavahi = abs(antar[i]) - abs(vartamanAntar);
                if (sahayakKaryavahi > 0) {
                    kulKaryavahi += sahayakKaryavahi;
                }
                vartamanAntar = antar[i];
            } else {
                kulKaryavahi += abs(antar[i]);
                vartamanAntar = antar[i];
            }
        }
        return kulKaryavahi;
    }

    bool pehlaYaSamaanAntar(int i, vector<int>& antar) {
        return (i == 0) || (antar[i - 1] > 0 && antar[i] > 0) || (antar[i - 1] < 0 && antar[i] < 0);
    }





};
