class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        long long ans = 0;
        int neg = 0, pos = 0;
        int prev = 0;
        for (int i = 0; i < n; ++i) {
            nums[i] = target[i] - nums[i];
            if (nums[i] < 0) {
                ans += max(0, neg - nums[i]);
                neg = nums[i];
                pos = 0;
            } else {
                ans += max(0, nums[i] - pos);
                pos = nums[i];
                neg = 0;
            }
        }
        return ans;
    }
};
