#define ll long long

class Solution {
public:
    ll minimumOperations(vector<int>& a, vector<int>& b) {
        ios_base::sync_with_stdio(false);
    cin.tie(NULL);
        ll n = a.size();
        deque<ll> d(n);
        for (ll i = 0; i < n; ++i) {
            d[i] = b[i] - a[i];
        }

        ll tot = 0;
        ll cur = 0;
        ll i = 0;

        while (i < n) {
            ll add = (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)) ? 
                      (abs(d[i]) - abs(cur)) : abs(d[i]);
            tot += (add > 0) ? add : 0;
            cur = d[i];
            ++i;
        }

        return tot;
    }
};
