#include<bits/stdc++.h>
#define LL long long
#define len(G) (LL)(G).size()
#define all(G) (G).begin(),(G).end()
using namespace std;
using i128=__int128;
using ar=array<LL,2>;
using Poly=vector<LL>;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        LL n=len(a);
        a.insert(a.begin(),0);
        b.insert(b.begin(),0);

        for(LL i=1;i<=n;++i)
        {
            b[i]-=a[i];
        }
        LL x[2]={};
        for(LL i=n;i>=1;--i)
        {
            b[i]-=b[i-1];
            x[b[i]>0]+=abs(b[i]);
        }
        return max(x[0],x[1]);
    }
};
