class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> sec;
        int n = nums.size();
        int flag = -2;
        for(int i = 0 ;i <n ; i++){
            if(flag == -2){
                if(nums[i] > target[i]) {
                    flag = 1;
                }
                else if(nums[i] == target[i]){
                    flag = 0;
                }else{
                    flag = -1;
                }
            }else{
                if(nums[i] > target[i]) {
                    if(flag != 1){
                        sec.push_back(i);
                        flag = 1;
                    }
                }
                else if(nums[i] == target[i]){
                    if(flag != 0){
                        sec.push_back(i);
                        flag = 0;
                    }
                }else{
                    if(flag != -1){
                        sec.push_back(i);
                        flag = -1;
                    }
                }
            }
        }
        sec.push_back(n);
        for(auto x : sec){
            cout<<x<<" ";
        }
        cout<<endl;
        long long ans = 0;
        for(int i = 0 ; i < sec.size() ; i++){
            vector<int> diff;
            for(int j = (i) > 0 ? sec[i-1]:0 ; j<sec[i] ; j++){
                // cout<<nums[j]<<" "<<target[j]<<endl;
                diff.push_back(abs(nums[j] - target[j]));
            }
            ans += diff[0];
            for(int j = 1 ; j < diff.size() ; j++){
                if(diff[j] > diff[j-1]){
                    ans += diff[j]-diff[j-1];
                }
            }
        }
        return ans;
    }
};
