#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int n=nums.size();
        ll res=0;
        ll diff=nums[0]-t[0];
        ll p=diff;
        for(int i=1;i<n;i++){
                ll z=nums[i]-t[i];
                if((z<0&&diff<0)||(z>0&&diff>0))
                {   if(abs(z)>abs(diff))
                    z=abs(diff-z);
                    else
                    z=0;
                }
                else{
                    z=abs(z); 
                }
             diff=nums[i]-t[i];

                res+=z;

        }
        res+=abs(p);
        return res;
    }
};
