class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int n = nums.size();
        vector<int>diff(n);
        bool a = false;
        long long int b = 0;
        long long int ans =0;
        for(int i=0;i<n;i++)
        {
            diff[i] = target[i]-nums[i];
        }
        for(int i=0;i<n;i++)
        {
            if(i==0)
            {
                ans  =abs(diff[0]);
                b=ans;
                if(diff[0]>=0)
                {
                    a=true;
                }
            }
            else
            {
                if(diff[i]>=0 && a==false)
                {
                    ans+=abs(diff[i]);
                    b= abs(diff[i]);
                    a=true;

                }
                else if(diff[i]<0 && a==true)
                {
                    ans+=abs(diff[i]);
                    b=abs(diff[i]);
                    a=false;
                }
                else if(b>=abs(diff[i]))
                {
                    b=abs(diff[i]);
                }
                else
                {
                    ans+=abs(diff[i])-b;
                    b=abs(diff[i]);
                }
            }
        }
        return ans;

    }
};
