class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        // Calculate the differences between nums and target elements
        for (int i = 0; i < nums.size(); i++) {
            target[i] = nums[i] - target[i];
        }

        int currentDifference = 0;
        long long operations = 0;

        for (int i = 0; i < nums.size(); i++) {
            // If the current target difference is zero, reset currentDifference and continue
            if (target[i] == 0) {
                currentDifference = 0;
                continue;
            }

            int difference = target[i];
            target[i] = 0; // Reset target[i] as it has been processed

            // Handling negative differences
            if (difference < 0) {
                if (difference < currentDifference && currentDifference < 0) {
                    operations += abs(difference - currentDifference);
                    currentDifference = difference;
                } else if (difference > currentDifference && currentDifference < 0) {
                    currentDifference = difference;
                } else if (currentDifference >= 0) {
                    operations += abs(difference);
                    currentDifference = difference;
                }
            }

            // Handling positive differences
            if (difference > 0) {
                if (difference > currentDifference && currentDifference > 0) {
                    operations += abs(difference - currentDifference);
                    currentDifference = difference;
                } else if (difference < currentDifference && currentDifference > 0) {
                    currentDifference = difference;
                } else if (currentDifference <= 0) {
                    operations += abs(difference);
                    currentDifference = difference;
                }
            }
        }
        return operations;
    }
};
