class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        target = [target[i] - nums[i] for i in range(len(nums))]

        cur = 0
        a = []
        res = 0

        for i in range(len(nums)):
            if cur == 0 and target[i] <= 0:
                if len(a) != 0:
                    res += a[0]
                    for j in range(1, len(a)):
                        if a[j] > a[j - 1]:
                            res += a[j] - a[j - 1]
                a.clear()
                cur = 1
            elif cur == 1 and target[i] >= 0:
                if len(a) != 0:
                    res += a[0]
                    for j in range(1, len(a)):
                        if a[j] > a[j - 1]:
                            res += a[j] - a[j - 1]
                a.clear()
                cur = 0

            if cur == 0:
                a.append(target[i])
            else:
                a.append(abs(target[i]))

        if len(a) != 0:
            res += a[0]
            for j in range(1, len(a)):
                if a[j] > a[j - 1]:
                    res += a[j] - a[j - 1]

        return res
