class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long ans = 0;
        int max = 0;
        for(int i = 0; i < n; i++) {
            int diff = target[i] - nums[i];
            if(diff >= 0 && max >= 0) {
                if(diff > max) {
                    ans += diff - max;
                }
                max = diff;
            } else if(diff < 0 && max < 0) {
                if(diff < max) {
                    ans += max - diff;
                }
                max = diff;
            } else {
                ans += Math.abs(diff);
                max = diff;
            }
        }
        return ans;
    }
}
