class Solution:



    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]
        # print(diff)
        pre = 0
        consec = []


        def cal(arr):
            # print("compute", arr)
            if not arr: return 0
            if arr and arr[0] < 0:
                arr = [-val for val in arr]

            ans = 0
            pre = 0
            for val in arr:
                if val > pre:
                    ans += val - pre
                pre = val
            return ans


        ans = 0
        for val in diff:
            if val * pre > 0:
                consec.append(val)
            else:
                ans += cal(consec)
                consec = [val]
            pre = val
        if consec:
            ans += cal(consec)


        return ans
