class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector<long long> cha(n + 10, 0);
        for (int i = 0; i < n; i ++ ) {
            int dis = target[i] - nums[i];

            cha[i] += dis;
            cha[i + 1] -= dis;
        }

        long long fu = 0, zheng = 0;
        for (int i = 0; i < n; i ++ ) {
            if (cha[i] < 0) fu -= cha[i];
            if (cha[i] > 0) zheng += cha[i];
        }

        return max(fu, zheng);
    }
};
