class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        for (int i=0; i<n; i++){
            nums[i] = target[i]-nums[i];
        }

        long long steps = 0;
        int prev = 0;
        for (int i=0; i<n; i++){
            if (nums[i] > 0 && prev > 0){
                if (prev < nums[i]){
                    steps += nums[i] - prev;
                }
                prev = nums[i];
            }
            else if (nums[i] < 0 && prev < 0){
                if (abs(prev) < abs(nums[i])){
                    steps += prev - nums[i];
                }
                prev = nums[i];
            }
            else {
                steps += abs(nums[i]);
                prev = nums[i];
            }
        }

        return steps;

    }
};
