class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i = n-1;i >= 1;i --)
        {
            nums[i] -= nums[i-1];
            target[i] -= target[i-1];
        }
        long long neg = 0,pos = 0;
        for(int i = 0;i < n;i ++)
        {
            long long dif = nums[i] - target[i];
            if(dif < 0)neg -= dif;
            else pos += dif;
        }
        return max(neg,pos);


    }
};
