class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n=nums.size();
        vector<int> t(n,0);
        for (int i=0;i<n;i++)
        {
            t[i]=tar[i]-nums[i];
        }
        int posi=0;
        long long  temp=0,ans=0;
        ans= abs(t[0]);
        temp=ans;
        if(t[0]>=0)
        posi=1;
        for(int i=1;i<n;i++)
        {
            if(t[i]<0 && posi==1)
            {
                ans+= abs(t[i]);
                temp= abs(t[i]);
                posi=0;
                continue;
            }
            if(t[i]>=0 && posi==0)
            {
                ans+= abs(t[i]);
                temp= abs(t[i]);
                posi=1;
                continue;
            }
            if(temp >= abs(t[i]))
            {

                temp=abs(t[i]);

                continue;
            }
            else
            {

                ans+= abs(t[i])-temp;
                temp=abs(t[i]);

            }
        }
        return ans;
    }
};
