class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> v(n);
            for (int i = 0; i < n; ++i) {
            v[i] = target[i] - nums[i];

            cout<<v[i]<<" ";
            }
            cout<<endl;

        long long ans = 0;
        vector<int> temp;
        temp.push_back(abs(v[0]));

        for (int i = 1; i < n; ++i) {
            if ((v[i] > 0 && v[i-1] > 0) || (v[i] < 0 && v[i-1] < 0) || (v[i] == 0 && v[i-1] == 0)) {
                temp.push_back(abs(v[i]));
            } else {
                // cout<<temp.size()<<" ";
                ans += cs(temp);
                // cout<<ans<<endl;
                temp.clear();
                temp.push_back(abs(v[i]));
            }
        }
        // cout<<temp.size();
        ans += cs(temp);
        // cout<<cs(temp);
        return ans;
    }

private:
    long long cs( vector<int>& temp) {
        int m = temp.size();
        long long cost = 0;
        int u = temp[0];

        for (int i1 = 1; i1 < m; ++i1) {
            if (temp[i1] >= u) {
                u = temp[i1];
            } else {
                cost += (u - temp[i1]);
                u=temp[i1];
            }
            if(temp[1]==1)
            cout<<cost<<" ";
        } 
        cost += u;
        return cost;
    }
};
