class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int left=0,right=0,flip=0,store=0;
        long long ans=0;
        for(int i=0;i<nums.size();i++){
            if(flip==0){
                if(target[i]>nums[i]) flip=1;
                else if(target[i]<nums[i]) flip=-1;
            }
            if(target[i]>nums[i] && flip==1){
                if(store>=target[i]-nums[i]){
                    store=target[i]-nums[i];
                    continue;
                } 
                else ans+=(target[i]-nums[i])-store;
                store=target[i]-nums[i];
            }
            else if(target[i]<nums[i] && flip==-1){
                if(store>=nums[i]-target[i]){
                    store=nums[i]-target[i];
                    continue;
                }
                else ans+=(nums[i]-target[i])-store;
                store=nums[i]-target[i];
            }
            else if(target[i]>nums[i] && flip==-1){
                flip=1;
                store=0;
                ans+=(target[i]-nums[i])-store;
                store+=target[i]-nums[i];
            }
            else if(target[i]<nums[i] && flip==1){
                flip=-1;
                store=0;
                ans+=(nums[i]-target[i])-store;
                store+=nums[i]-target[i];
            }
            else if(target[i]==nums[i]){
                flip=0;
                store=0;
            }
            // cout<<ans<<" "<<flip<<" "<<store<<endl;

        }
        return ans;
