class Solution {
public:
    long long minimumOperations(vector<int>& n, vector<int>& t) {
        vector<int> d(n.size());
        for (size_t i = 0; i < n.size(); ++i) d[i]=t[i]-n[i];
        long long ops = 0;
        long long cur_diff = 0;
        for (size_t i = 0; i < d.size(); ++i) {
            if (i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)) {
                int add_ops = abs(d[i]) - abs(cur_diff);
                if (add_ops > 0) {
                    ops += add_ops;
                }
                cur_diff = d[i];
            } else {
                ops += abs(d[i]);
                cur_diff = d[i];
            }
        }

        return ops;
    }
};
