func minimumOperations(nums []int, target []int) int64 {
  var delta = make([]int, len(nums))
  for i := 0; i < len(nums); i++ {
    delta[i] = target[i] - nums[i]
  }

  var ops int64 = 0
  prior := 0
  for i := 0; i < len(delta); i++ {
    if delta[i] > 0 {
      if prior < 0 {
        prior = 0
      }
      if delta[i] > prior {
        ops += int64(delta[i] - prior)
      }
      prior = delta[i]
    } else {
      if prior > 0 {
        prior = 0
      }
      if delta[i] < prior {
        ops += int64(prior - delta[i])
      }
      prior = delta[i]
    }
  }
  return ops
}
