class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        differences = [0] * n
        for i in range(n):
            differences[i] = target[i] - nums[i]

        total_ops = 0
        segment_diff = 0
        for i in range(n):
            current_diff = differences[i]
            if i == 0 or (segment_diff > 0 and current_diff > 0) or (segment_diff < 0 and current_diff < 0):
                required_ops = abs(current_diff) - abs(segment_diff)
                if required_ops > 0:
                    total_ops += required_ops
                segment_diff = current_diff
            else:
                total_ops += abs(current_diff)
                segment_diff = current_diff

        return total_ops
