class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        last_operation = 0
        for i in range(len(nums)):
            if nums[i] != target[i]:
                if last_operation * (target[i] - nums[i]) > 0:
                    if abs(last_operation) < abs(target[i] - nums[i]):
                        ans += (abs(target[i] - nums[i]) - abs(last_operation))
                else:
                    ans += abs(target[i] - nums[i])
            last_operation = target[i] - nums[i]
        return ans
