using LL = long long;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n = nums.size();

        std::vector<LL> d1(n + 1);
        std::vector<LL> d2(n + 1);
        for (int i = 0; i < n; ++i) {
            if (!i) {
                d1[i] = nums[i];
                d2[i] = target[i];
            } else {
                d1[i] = nums[i] - nums[i - 1];
                d2[i] = target[i] - target[i - 1];
            }
        }

        std::vector<LL> d(n + 1);
        for (int i = 0; i < n; ++i)
            d[i] = d1[i] - d2[i];

        LL res = 0;
        LL cnta = 0, cntb = 0;
        for (int i = 0; i < n; ++i) {
            if (d[i] >= 0) {
                LL t = cntb - d[i];
                if (t >= 0) {
                    cntb = t;
                } else {
                    res += -t;
                    cnta += -t;
                    cntb = 0;
                }
            } else {
                LL t = cnta - (-d[i]);
                if (t >= 0) {
                    cnta = t;
                } else {
                    res += -t;
                    cntb += -t;
                    cnta = 0;
                }
            }
        }   

        return res;
    }
};
